import React, { useState } from "react";
import { db } from "../firebase-init";
import {
  collection,
  addDoc,
  updateDoc,
  doc,
  serverTimestamp,
  arrayUnion,
} from "firebase/firestore";
import { useNavigate } from "react-router-dom";
import Event from "./Event";

const CreateEvent = ({ user }) => {
  const navigate = useNavigate();
  const [showPreview, setPreview] = useState(false);

  const [formData, setFormData] = useState({
    name: "",
    desc: "",
    start_date: "",
    end_date: "",
    address: "",
    location: [0, 0], // Assuming initial location
    media_url: [],
    post_event: "",
    attendees: 0,
    rating: 0,
    social: {
      facebook: "",
      twitter: "",
      instagram: "",
    },
    status: 0,
  });

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (name.includes("social")) {
      const n = name.split(".")[1];
      setFormData({
        ...formData,
        social: {
          ...formData.social,
          [n]: value,
        },
      });
    } else {
      setFormData({
        ...formData,
        [name]: value,
      });
    }
  };
  

  const handleLocationChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      location: value.split(",").map((coord) => parseFloat(coord.trim())),
    });
  };

  const handleMediaChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value.split(",").map((url) => url.trim()),
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      // Add a new document to the "Events" collection
      const eventsCollection = collection(db, "Events");
      const newEventRef = await addDoc(eventsCollection, {
        ...formData,
        uid: user.uid,
        status: 0,
        created_at: serverTimestamp(),
      });

      // Update the "managed" array of the user with the new event ID
      const userDocRef = doc(db, "Users", user.uid);
      await updateDoc(userDocRef, {
        managed: arrayUnion(newEventRef.id),
      });

      // Navigate to the home page
      navigate("/");
    } catch (error) {
      console.error("Error creating event:", error);
      // Handle error
    }
  };
  return (
    <div className="container mt-5">
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Event Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Event Description:</label>
          <textarea
            name="desc"
            value={formData.desc}
            onChange={handleInputChange}
            className="form-control"
            required
          ></textarea>
        </div>
        <div className="mb-3">
          <label className="form-label">Start Date:</label>
          <input
            type="datetime-local"
            name="start_date"
            value={formData.start_date}
            onChange={handleInputChange}
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">End Date:</label>
          <input
            type="datetime-local"
            name="end_date"
            value={formData.end_date}
            onChange={handleInputChange}
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Address:</label>
          <input
            type="text"
            name="address"
            value={formData.address}
            onChange={handleInputChange}
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Location (Latitude, Longitude):</label>
          <input
            type="text"
            name="location"
            value={formData.location.join(", ")}
            onChange={handleLocationChange}
            className="form-control"
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Media URLs (comma-separated):</label>
          <input
            type="text"
            name="media_url"
            value={formData.media_url.join(", ")}
            onChange={handleMediaChange}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Post Event:</label>
          <textarea
            name="post_event"
            value={formData.post_event}
            onChange={handleInputChange}
            className="form-control"
          ></textarea>
        </div>
        <div className="mb-3">
          <label className="form-label">Rating:</label>
          <input
            type="number"
            name="rating"
            value={formData.rating}
            onChange={handleInputChange}
            className="form-control"
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Social Handles:</label>
          <div className="mb-3">
            <label className="form-label">Facebook:</label>
            <input
              type="text"
              name="social.facebook"
              value={formData.social.facebook}
              onChange={handleInputChange}
              className="form-control"
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Twitter:</label>
            <input
              type="text"
              name="social.twitter"
              value={formData.social.twitter}
              onChange={handleInputChange}
              className="form-control"
            />
          </div>
          <div className="mb-3">
            <label className="form-label">Instagram:</label>
            <input
              type="text"
              name="social.instagram"
              value={formData.social.instagram}
              onChange={handleInputChange}
              className="form-control"
            />
          </div>
        </div>
        <div className="row">
            <div className="mb-3">
            <button onClick={()=>{setPreview(!showPreview)}} className="btn btn-primary">
                Preview Event
            </button>
            </div>
            <div className="mb-3">
            <button type="submit" className="btn btn-primary">
                Create Event
            </button>
            </div>
        </div>
        {showPreview &&
            <>
            <h3>Preview: </h3>
            <div className="mb-3">
            <Event event={formData} />
            </div>
            </>
        }
        </form>
    </div>
  );
};

export default CreateEvent;
